package com.l3.logparser.pnr.model;

/**
 * Model representing PNR-specific details extracted from logs
 */
public class PnrDetails {
    private String pnrReference;
    private String bookingDate;
    private String passengerCount;
    private String flightDetails;
    private String bookingStatus;

    public PnrDetails() {}

    // Getters and Setters
    public String getPnrReference() { return pnrReference; }
    public void setPnrReference(String pnrReference) { this.pnrReference = pnrReference; }

    public String getBookingDate() { return bookingDate; }
    public void setBookingDate(String bookingDate) { this.bookingDate = bookingDate; }

    public String getPassengerCount() { return passengerCount; }
    public void setPassengerCount(String passengerCount) { this.passengerCount = passengerCount; }

    public String getFlightDetails() { return flightDetails; }
    public void setFlightDetails(String flightDetails) { this.flightDetails = flightDetails; }

    public String getBookingStatus() { return bookingStatus; }
    public void setBookingStatus(String bookingStatus) { this.bookingStatus = bookingStatus; }

    @Override
    public String toString() {
        return "PnrDetails{" +
                "pnrReference='" + pnrReference + '\'' +
                ", bookingDate='" + bookingDate + '\'' +
                ", passengerCount='" + passengerCount + '\'' +
                ", bookingStatus='" + bookingStatus + '\'' +
                '}';
    }
}

