import com.l3.logparser.api.parser.ApiParser;
import com.l3.logparser.api.model.EdifactMessage;
import java.util.List;

/**
 * Simple demonstration of the refactored two-stage ApiParser
 */
public class ApiParserTestDemo {

    public static void main(String[] args) {
        ApiParser parser = new ApiParser();

        // Sample log content with EDIFACT message
        String logContent = "2024-11-30 10:15:23 INFO  [Thread-1] com.example.Forward.BUSINESS_RULES_PROCESSOR - Message body [UNA:+.?'UNB+IATB:1+6X+FRAORD+240725+1130+TS2302507251130'UNH+TS2302507251130+PAXLST:D:05B:UN:IATA+TS2302507251130'BGM+745+TS2302507251130'TDT+20+MS775++MS'LOC+125+FRA'LOC+87+ORD'DTM+189:24072511:30'UNZ+1+TS2302507251130']";

        // Test parsing with debug mode
        List<EdifactMessage> messages = parser.parseLogContent(logContent, "MS775", true, 
            debugMsg -> System.out.println("[DEBUG] " + debugMsg));

        System.out.println("\n=== PARSING RESULTS ===");
        System.out.println("Found " + messages.size() + " messages");

        for (int i = 0; i < messages.size(); i++) {
            EdifactMessage msg = messages.get(i);
            System.out.println("\nMessage " + (i + 1) + ":");
            System.out.println("  Flight Number: " + msg.getFlightNumber());
            System.out.println("  Message ID: " + msg.getMessageId());
            System.out.println("  Data Type: " + msg.getDataType());
            System.out.println("  Part Number: " + msg.getPartNumber());
            System.out.println("  Part Indicator: " + msg.getPartIndicator());

            if (msg.getFlightDetails() != null) {
                System.out.println("  Flight Details:");
                System.out.println("    Departure: " + msg.getFlightDetails().getDepartureAirport());
                System.out.println("    Arrival: " + msg.getFlightDetails().getArrivalAirport());
                System.out.println("    Departure Date: " + msg.getFlightDetails().getDepartureDate());
                System.out.println("    Departure Time: " + msg.getFlightDetails().getDepartureTime());
            }

            System.out.println("  Raw Content Length: " + 
                (msg.getRawContent() != null ? msg.getRawContent().length() : 0) + " chars");
        }

        System.out.println("\n=== TWO-STAGE PARSING COMPLETE ===");
        System.out.println("Stage 1: Extract message boundaries (start patterns to UNZ)");
        System.out.println("Stage 2: Parse each message character-by-character using separators");
    }
}

