package com.l3.logextractor.test;

import javafx.application.Application;
import javafx.fxml.FXMLLoader;
import javafx.scene.Scene;
import javafx.stage.Stage;

/**
 * Simple test application to verify FXML loads correctly
 */
public class FXMLTestApp extends Application {

    @Override
    public void start(Stage primaryStage) {
        try {
            FXMLLoader loader = new FXMLLoader(getClass().getResource("/com/l3/rcaengine/api/log-extraction-view.fxml"));
            Scene scene = new Scene(loader.load(), 1025, 916);

            primaryStage.setTitle("Log Extraction Module - Test");
            primaryStage.setScene(scene);
            primaryStage.show();

            System.out.println("FXML loaded successfully!");

        } catch (Exception e) {
            System.err.println("Error loading FXML: " + e.getMessage());
            e.printStackTrace();
        }
    }

    public static void main(String[] args) {
        launch(args);
    }
}
